mapper.select:
SELECT *
  FROM my_security
 WHERE code = #{code}
   AND language_id = #{language_id}


SELECT *
  FROM my_security_contents
 WHERE language_id = #{language_id}
   AND code = #{code}

   AND title LIKE concat('%',#{title},'%')

 ORDER BY title ASC

SELECT contents_id, title
  FROM my_security_contents
 WHERE language_id = #{language_id}
   AND code = #{code}

   AND title LIKE concat('%',#{title},'%')

 ORDER BY title ASC

SELECT *
  FROM my_security
 WHERE code = #{code}
   AND language_id = #{language_id}

SELECT *
  FROM my_security_contents
 WHERE contents_id = #{contents_id}
   AND language_id = #{language_id}
   AND code = #{code}

SELECT COUNT(*) AS total
  FROM my_security_contents
 WHERE contents_id = #{contents_id}
   AND language_id = #{language_id}
   AND code = #{code}
